import * as types from '../types'
import Key from '../../key.json';
import axios from 'axios'
import fetcher from '../fetcher'


export const fetchposts = () => async dispatch => {
    const response = await axios({
            method: 'get', 
            url: 'https://cors-anywhere.herokuapp.com/https://api.twitter.com/1.1/statuses/user_timeline.json?screen_name=realDonaldTrump&count=1',
            mode: 'no-cors',
            headers: {
                Authorization: Key.key,
                'Access-Control-Allow-Origin': '*',
            }
        })

    const stringResponse = JSON.stringify(response.data)

    dispatch({
        type: types.GET_POSTS,
        payload: stringResponse
    })
}

export const setposts = () => async dispatch => {
    const response = await axios({
            method: 'get', 
            url: 'https://cors-anywhere.herokuapp.com/https://api.twitter.com/1.1/statuses/user_timeline.json?screen_name=HillaryClinton&count=1',
            mode: 'no-cors',
            headers: {
                Authorization: Key.key,
                'Access-Control-Allow-Origin': '*',
            }
        })

    const stringResponse = JSON.stringify(response.data)

    dispatch({
        type: types.SET_POSTS,
        payload:stringResponse
    })
}
