import { useDispatch, useSelector } from 'react-redux'
import { fetchTrumpTweets, fetchHillaryTweets } from '../redux/actions/tweetAction'

export default function Home() {

    const dispatch = useDispatch();
    const { tweets } = useSelector(state => state.tweets);
    let tweetJSON = { tweets }

    if(isJson(tweetJSON))
        tweetJSON = JSON.parse(tweetJSON.tweets)

    console.log(tweetJSON) 

    return (
        <div>
            <div>
                <button onClick={() => dispatch(fetchTrumpTweets())}>
                    Show Trump's Tweets
                </button>
                <button onClick={() => dispatch(fetchHillaryTweets())}>
                    Show Hillary's Tweets
                </button>
            </div>
            <div>
                {tweetJSON}
            </div>
        </div>
    )

}

export function isJson(str){
    try {
        JSON.parse(str);
    } catch (e) {
        return false;
    }
    return true;
}
