import * as types from '../types'
import Key from '../../key.json';
import axios from 'axios'
import fetcher from '../fetcher'


export const fetchposts = () => async dispatch => {
        dispatch({
        type: types.GET_POSTS,
        payload: ['post']
    })
}

export const setposts = () => async dispatch => {
    //Pulling From the Twitter API Resulted in a status 400, 
    //with no real cause. Authorizations worked as expect but request was aborted regardless
    //
    // const trumpEndpoint = 'https://api.twitter.com/1.1/statuses/user_timeline.json?screen_name=realDonaldTrump&count=1';
    // let response = await fetch(trumpEndpoint,{
    //      headers: {
    //             'Authorization': Key.key
    //         },
    //         mode: 'no-cors'
    // })

    //const res = await axios.get('https://jsonplaceholder.typicode.com/comments')

    const res = await axios({
            method: 'get', //you can set what request you want to be
            url: 'https://api.twitter.com/1.1/statuses/user_timeline.json?screen_name=realDonaldTrump&count=1',
            mode: 'no-cors',
            headers: {
                Authorization: Key.key,
                'Access-Control-Allow-Origin': '*',
            }
        })

    dispatch({
        type: types.SET_POSTS,
        payload:res.data
    })
}
