import * as types from '../types'

const initialState = {
    tweets: ["Who's Tweets Would You Like To See"],
    dates: [""],
    user: "Please Select Who's Tweets You Would Like to See"
}

export const tweetReducer = (state = initialState, action) => {
    switch (action.type){
        case types.TRUMP_TWEETS:
            return {
                ...state,
                tweets: action.payload,
                dates: action.dates,
                user: "Viewing Donald Trump's Tweets"
            }
        case types.HILLARY_TWEETS:
            return {
                ...state,
                tweets: action.payload,
                dates: action.dates,
                user: "Viewing Hillary Clinton's Tweets"
            }
        default:
            return state
    }
}
