import { useDispatch, useSelector } from 'react-redux'
import { fetchTrumpTweets, fetchHillaryTweets } from '../redux/actions/tweetAction'

export default function Home() {

    const dispatch = useDispatch();
    const { tweeter } = useSelector(state => state.tweeter);
    let tweetJSON = { tweeter}

    console.log("before entering")
    if(isJson(tweetJSON)){
        tweetJSON = JSON.parse(tweetJSON.tweets)  
    }

    return (
        <div>
            <div>
                <button onClick={() => dispatch(fetchTrumpTweets())}>
                    Show Trump's Tweets
                </button>
                <button onClick={() => dispatch(fetchHillaryTweets())}>
                    Show Hillary's Tweets
                </button>
            </div>
            <div>
                {tweetJSON.tweeter.user}
            </div>
        </div>
    )

}

export function isJson(str){
    console.log("entered function")
    try {
        JSON.parse(str);
    } catch (e) {
        console.log("flase")
        return false;
    }
    console.log("true")
    return true;
}
