import { useDispatch, useSelector } from 'react-redux'
import { fetchTrumpTweets, fetchHillaryTweets } from '../redux/actions/tweetAction'
import { Container, Row, Card, Button } from 'react-bootstrap'
import React from 'react';


export default function Home() {

    const dispatch = useDispatch();
    const tweeter  = useSelector(state => state);
    //const { user } = useSelector(state => state.user)
    let tweetJSON = { tweeter}

    console.log(tweeter.tweeter)
    if(isJson(tweetJSON)){
        tweetJSON = JSON.parse(tweetJSON.tweeter)  
    }

    return (
        // <div className="container">
        //     <div>
        //         <h1>Tweet Fetcher</h1>
        //         <Button variant="primary" onClick={() => dispatch(fetchTrumpTweets())}>
        //             Show Trump's Tweets
        //         </Button>
        //         <Button onClick={() => dispatch(fetchHillaryTweets())}>
        //             Show Hillary's Tweets
        //         </Button>
        //     </div>
        //     <div>
        //         {tweeter.tweeter.user}
        //     </div>

        //     <style jsx>{`
        //         .container {
        //         margin: 50px;
        //         }
        //         p {
        //         color: blue;
        //         }
        //     `}</style>

        // </div>
        <Container className="md-container">
      <Container>
        <h1>
          Welcome to Tweet Fetcher
        </h1>
        <p>
          Get started by editing <code>pages/index.js</code>
        </p>
        <Container>
          <Row className="justify-content-md-between">
            <Card className="sml-card">
              <Card.Body>
                <Card.Title>Documentation</Card.Title>
                <Card.Text>
                  Find in-depth information about Next.js features and API.
                </Card.Text>
                <Button variant="primary" href="https://nextjs.org/docs">
                  More &rarr;
                </Button>
              </Card.Body>
            </Card>
            <Card className="sml-card">
              <Card.Body>
                <Card.Title>Learn</Card.Title>
                <Card.Text>
                  Learn about Next.js in an interactive course with quizzes!
                </Card.Text>
                <Button variant="primary" href="https://nextjs.org/learn">
                  More &rarr;
                </Button>
              </Card.Body>
            </Card>
          </Row>
          <Row className="justify-content-md-between">
            <Card className="sml-card">
              <Card.Body>
                <Card.Title>Examples</Card.Title>
                <Card.Text>
                  Discover and deploy boilerplate example Next.js projects.
                </Card.Text>
                <Button
                  variant="primary"
                  href="https://github.com/vercel/next.js/tree/master/examples"
                >
                  More &rarr;
                </Button>
              </Card.Body>
            </Card>
            <Card className="sml-card">
              <Card.Body>
                <Card.Title>Deploy</Card.Title>
                <Card.Text>
                  Instantly deploy your Next.js site to a public URL with
                  Vercel.
                </Card.Text>
                <Button
                  variant="primary"
                  href="https://vercel.com/import?filter=next.js&utm_source=github&utm_medium=example&utm_campaign=next-example"
                >
                  More &rarr;
                </Button>
              </Card.Body>
            </Card>
          </Row>
        </Container>
      </Container>
    </Container>
    )
}

export function isJson(str){
    console.log("entered function")
    try {
        JSON.parse(str);
    } catch (e) {
        console.log("flase")
        return false;
    }
    console.log("true")
    return true;
}
