import React from 'react';
import {connect} from 'react-redux';
import {hillarysTweets, trumpsTweets} from '../redux/actions/tweetActions';

class App extends React.Component {

    static getInitialProps({store}) {}

    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div>
                <button onClick={this.props.trumpsTweets}>Increment</button>
                <button onClick={this.props.hillarysTweets}>Decrement</button>
                <h1>{this.props.tweet}</h1>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    tweet: state.tweet.value
});

const mapDispatchToProps = {
    trumpsTweets: trumpsTweets,
    hillarysTweets: hillarysTweets,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
