import { useDispatch, useSelector } from 'react-redux'
import { fetchTrumpTweets, fetchHillaryTweets } from '../redux/actions/tweetAction'
import { Container, Row, Card, Button, Jumbotron } from 'react-bootstrap'
import React from 'react';


export default function Home() {

    const dispatch = useDispatch();
    const tweeter  = useSelector(state => state);
    //const { user } = useSelector(state => state.user)
    //let tweetJSON = { tweeter}

    console.log(tweeter.tweeter)
    // if(isJson(tweetJSON)){
    //     tweetJSON = JSON.parse(tweetJSON.tweeter)  
    // }

    return (
        <Container className="md-container">
          <Container>
              <Jumbotron className="text-center" >
                <Container>
                  <h1 className="title" style={{ fontSize: "50px" }}>Welcome To The Tweet Fetcher</h1>
                  <h5 style={{ margin: " 0 0 20px 0" }}>{tweeter.tweeter.info}</h5>
                  
                    <Button variant="danger active" block onClick={() => dispatch(fetchTrumpTweets())}>
                        Show Trump's Tweets
                    </Button>
                    <Button variant="primary active" block onClick={() => dispatch(fetchHillaryTweets())}>
                        Show Hillary's Tweets
                    </Button>
                </Container>
              </Jumbotron>
            {/* <Container>
              <Row className="justify-content-md-between">
                {tweeter.tweeter.tweets.map(function(tweet, index){
                  return(
                    <Card className="sml-card" block style={{ margin: "10px", width:"100%" }} key={ index }>
                      <Card.Header>Tweet #{ index + 1 }</Card.Header>
                      <Card.Body>
                        <Card.Title>{ tweet }</Card.Title>
                        <Card.Text>
                          {tweeter.tweeter.dates[index]}
                        </Card.Text>
                      </Card.Body>
                    </Card>
                  );
                })}
              </Row>
        </Container> */}
      </Container>
    </Container>
    )
}

