import { useDispatch, useSelector } from 'react-redux'
import { fetchTrumpTweets, fetchHillaryTweets } from '../redux/actions/tweetAction'


export default function Home() {

    const dispatch = useDispatch();
    const { tweet } = useSelector(state => state.tweets);

    
    return (
        <div>
            <div>
                <button onClick={() => dispatch(fetchTrumpTweets())}>
                    post
                </button>
                <button onClick={() => dispatch(fetchHillaryTweets())}>
                    set
                </button>
            </div>
            <div>
                {tweets}
            </div>
        </div>
    )
}
