import React, { Component } from 'react'
import fetch from 'isomorphic-unfetch'

export default class extends Component {
  static async getInitialProps() {
    const res = await fetch("https://api.twitter.com/1.1/statuses/user_timeline.json?screen_name=realDonaldTrump&count=1")
    const data = await res.json()
    console.log(data)
    console.log("hello world")

    return {
      date: data.created_at,
      text: data.text
    }
  }

  render () {
    return (
      <div>
        <div>
          {this.props.text}
        </div>
        <div>
          {this.props.date}
        </div>
      </div>
    )
  }
}
