import {TRUMPS_TWEETS, HILLARYS_TWEETS} from '../actions/tweetActions';

const tweetReducer = (state = {value: 0}, action) => {
    switch(action.type){
        case TRUMPS_TWEETS:
            return {...state, value: state.value + 1}
        case HILLARYS_TWEETS:
            return {...state, value: state.value - 1}
        default:
            return {...state};

    }
};

export default tweetReducer;
