import { useDispatch, useSelector } from 'react-redux'
import { fetchTrumpTweets, fetchHillaryTweets } from '../redux/actions/tweetAction'
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';

export default function Home() {

    const dispatch = useDispatch();
    const tweeter  = useSelector(state => state);
    //const { user } = useSelector(state => state.user)
    let tweetJSON = { tweeter}

    console.log(tweeter.tweeter)
    if(isJson(tweetJSON)){
        tweetJSON = JSON.parse(tweetJSON.tweeter)  
    }

    return (
        <div className="container">
            <div>
                <h1>Tweet Fetcher</h1>
                <Button onClick={() => dispatch(fetchTrumpTweets())}>
                    Show Trump's Tweets
                </Button>
                <Button onClick={() => dispatch(fetchHillaryTweets())}>
                    Show Hillary's Tweets
                </Button>
            </div>
            <div>
                {tweeter.tweeter.user}
            </div>

            <style jsx>{`
                .container {
                margin: 50px;
                }
                p {
                color: blue;
                }
            `}</style>

        </div>
    )

}

export function isJson(str){
    console.log("entered function")
    try {
        JSON.parse(str);
    } catch (e) {
        console.log("flase")
        return false;
    }
    console.log("true")
    return true;
}
